mapper.update:
UPDATE `room`
SET need_number = need_number + #{forwardNum}
WHERE
    id = #{roomId}

mapper.select:
SELECT * FROM room WHERE 1 = 1 and status = 1
and script = #{filed}
Order By start_time Desc LIMIT #{pageCount},5;

SELECT r.id AS id, r.has_number AS hasNumber, r.need_number AS needNumber, r.create_time AS createTime, r.script AS script, r.STATUS AS STATUS, r.id AS roomId, r.host_id AS hostId, r.start_time AS startTime, r.category_id AS categoryId, r.end_time AS endTime, s.script_name AS scriptName FROM room r LEFT JOIN script s ON s.id = r.script WHERE 1 = 1
and r.category_id = #{category}
AND r.STATUS = 1 LIMIT 0,5;
